Deno.serve(async (req) => {
  if (req.method === 'OPTIONS') {
    return new Response('ok', { headers: corsHeaders })
  }

  try {
    const supabaseClient = createClient(
      Deno.env.get('SUPABASE_URL') ?? '',
      Deno.env.get('SUPABASE_SERVICE_ROLE_KEY') ?? ''
    )

    // Check if questions already exist
    const { count } = await supabaseClient
      .from('questions')
      .select('*', { count: 'exact', head: true })

    if (count && count > 0) {
      return new Response(
        JSON.stringify({ message: 'Questions already seeded', count }),
        { headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
      )
    }

    // Convert options to the correct format and map correctAnswer to index
    const questionsToInsert = questionPool.map(q => {
      const answerMap: Record<string, number> = { 'A': 0, 'B': 1, 'C': 2, 'D': 3 }
      return {
        id: q.id,
        question: q.question,
        options: q.options,
        correct_answer: answerMap[q.correctAnswer],
        subelement: q.subelement,
        question_group: q.group,
      }
    })

    const { data, error } = await supabaseClient
      .from('questions')
      .insert(questionsToInsert)
      .select()

    if (error) throw error

    return new Response(
      JSON.stringify({ message: 'Questions seeded successfully', count: questionsToInsert.length }),
      { headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
    )
  } catch (error) {
    const errorMessage = error instanceof Error ? error.message : 'Unknown error'
    return new Response(
      JSON.stringify({ error: errorMessage }),
      { headers: { ...corsHeaders, 'Content-Type': 'application/json' }, status: 500 }
    )
  }
})